import './App.css';
import { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import { ChakraProvider, Table, Thead, Tbody, Tfoot, Tr, Th, Td, TableContainer } from '@chakra-ui/react';
import {Modal,ModalOverlay,ModalContent,ModalHeader, ModalFooter, ModalBody, ModalCloseButton,} from '@chakra-ui/react'
function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState(0);

  const [employeesList, setEmployeesList] = useState([]);

  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', {
        name,
        age,
        city,
        charge,
        years
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user registration:', error);
    }
  };

  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  // Llama a getEmployees al montar el componente
  useEffect(() => {
    getEmployees();
  }, []);

  function BasicUsage() {
    const { isOpen, onOpen, onClose } = useDisclosure()
    return (
      <>
        <Button onClick={onOpen}>Open Modal</Button>

        <Modal isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Modal Title</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Lorem count={2} />
            </ModalBody>

            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={onClose}>
                Close
              </Button>
              <Button variant='ghost'>Secondary Action</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    )
  }

  return (
    <ChakraProvider>
      <div className="App">
        <div className="data-form">
          <label>
            Name:
            <input
              onChange={(event) => setName(event.target.value)}
              type="text"
              value={name}
            />
          </label>

          <label>
            Age:
            <input
              onChange={(event) => setAge(parseInt(event.target.value, 10) || 0)}
              type="number"
              value={age}
            />
          </label>

          <label>
            City:
            <input
              onChange={(event) => setCity(event.target.value)}
              type="text"
              value={city}
            />
          </label>

          <label>
            Charge:
            <input
              onChange={(event) => setCharge(event.target.value)}
              type="text"
              value={charge}
            />
          </label>

          <label>
            Years:
            <input
              onChange={(event) => setYears(parseInt(event.target.value, 10) || 0)}
              type="number"
              value={years}
            />
          </label>

          <button colorScheme='blue' size="lg" className='buttonList' onClick={handleSubmit}>Insert</button>
        </div>
        <br />
        <div className="list">
          {employeesList.length > 0 ? (
            <TableContainer>
              <Table size='sm'>
                <Thead>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {employeesList.map((employee, index) => (
                    <Tr key={index}>
                      <Td>{employee.name}</Td>
                      <Td>{employee.age}</Td>
                      <Td>{employee.city}</Td>
                      <Td>{employee.charge}</Td>
                      <Td>{employee.years}</Td>
                      <Td>{employee.dateRegister.slice(0, -14)}</Td>
                    </Tr>
                  ))}
                </Tbody>
                <Tfoot>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                  </Tr>
                </Tfoot>
              </Table>
            </TableContainer>
          ) : (
            <div>No employees found</div>
          )}
        </div>
      </div>
    </ChakraProvider>
  );
}

export default App;
