import './App.css';
import { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import { ChakraProvider, Table, Thead, Tbody, Tfoot, Tr, Th, Td, TableContainer,Button, Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton } from '@chakra-ui/react';
import { PlusSquareIcon } from '@chakra-ui/icons'
import { Input } from '@chakra-ui/react'
import {
  FormControl,
} from '@chakra-ui/react'


function App() {


  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState("");

  const [employeesList, setEmployeesList] = useState([]);

  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', {
        name,
        age,
        city,
        charge,
        years
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user registration:', error);
    }
  };

  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };



  // Llama a getEmployees al montar el componente
  useEffect(() => {
    getEmployees();
  }, []);

  function BasicUsage() {
    const { isOpen, onOpen, onClose } = useDisclosure()
  return (
    <ChakraProvider>
      <div className="App">
      <FormControl isRequired>
        <div className="data-form">

            <Input
            placeholder='Name'
            variant='flushed'
            size='sm'
              onChange={(event) => setName(event.target.value)}
              type="text"
              value={name}
            />

          <Input
  placeholder="Age"
  variant="flushed"
  size="sm"
  onChange={(event) => setAge(parseInt(event.target.value))}
  type="number"
  value={age}
  min={1}
/>




            <Input
              placeholder="City"
            variant='flushed'
            size='sm'
              onChange={(event) => setCity(event.target.value)}
              type="text"
              value={city}
            />



            <Input
              placeholder="Charge"
            variant='flushed'
            size='sm'
              onChange={(event) => setCharge(event.target.value)}
              type="text"
              value={charge}
            />



            <Input
              placeholder="Years"
            variant='flushed'
            size='sm'
              onChange={(event) => setYears(parseInt(event.target.value, 10) || 0)}
              type="number"
              value={years}
            />

<br/>
          <Button  size='sm' rightIcon={<PlusSquareIcon />} colorScheme='blue' className='buttonList' onClick={handleSubmit}>Insert</Button>
        </div>
        </FormControl>
        <br />
        <div className="list">
          {employeesList.length > 0 ? (
            <TableContainer>
              <Table size='sm'>
                <Thead>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {employeesList.map((employee, index) => (
                    <Tr key={index}>
                      <Td>{employee.name}</Td>
                      <Td>{employee.age}</Td>
                      <Td>{employee.city}</Td>
                      <Td>{employee.charge}</Td>
                      <Td>{employee.years}</Td>
                      <Td>{employee.dateRegister.slice(0, -14)}</Td>
                    </Tr>
                  ))}
                </Tbody>
                <Tfoot>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                  </Tr>
                </Tfoot>
              </Table>
            </TableContainer>
          ) : (
            <div>No employees found</div>
          )}
        </div>
      </div>
    </ChakraProvider>
  );
}
}
export default App;
