import './App.css';
import { useState, useEffect, useRef } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import {
  IconButton,
  Wrap,
  Avatar,
  WrapItem,
  Text,
  Box,
  ChakraProvider,
  Table,
  Thead,
  Tbody,
  Tfoot,
  Tr,
  Th,
  Td,
  TableContainer,
  Button,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
  AlertDialogCloseButton,
  Flex,
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Input,
  FormControl
} from '@chakra-ui/react';
import { AddIcon, EditIcon, DeleteIcon, SettingsIcon } from '@chakra-ui/icons';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState("");
  const [image, setImage] = useState(null); // Estado para la imagen
  const [employeesList, setEmployeesList] = useState([]);
  const [employeeName, setEmployeeName] = useState("");
  const [employeeAge, setEmployeeAge] = useState("");
  const [employeeCity, setEmployeeCity] = useState("");
  const [employeeCharge, setEmployeeCharge] = useState("");
  const [employeeYears, setEmployeeYears] = useState("");
  const [employeeImage, setEmployeeImage] = useState(null);
  const [deleteId, setDeleteId] = useState(null); // Para almacenar el ID del registro a eliminar
  const [updateId, setUpdateId] = useState(null); // Para almacenar el ID del registro a actualizar

  // Hooks para modales
  const { isOpen: isAddModalOpen, onOpen: onAddModalOpen, onClose: onAddModalClose } = useDisclosure();
  const { isOpen: isDeleteModalOpen, onOpen: onDeleteModalOpen, onClose: onDeleteModalClose } = useDisclosure();
  const { isOpen: isUpdateModalOpen, onOpen: onUpdateModalOpen, onClose: onUpdateModalClose } = useDisclosure();
  const { isOpen: isDrawer, onOpen: onDrawerOpen, onClose: onDrawerClose } = useDisclosure()
  const btnRef = useRef()
  const cancelRef = useRef();

  // Función para enviar el formulario de agregar
  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('city', city);
    formData.append('charge', charge);
    formData.append('years', years);
    if (image) {
      formData.append('image', image); // Añadir la imagen al FormData
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();

        // Reset form fields
        setName("");
        setAge("");
        setCity("");
        setCharge("");
        setYears("");
        setImage(null); // Reset image

        // Close the modal
        onAddModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user registration:', error);
    }
  };

  // Función para enviar el formulario de actualización
  const handleSubmitUpdate = async () => {
    if (!employeeName || !employeeCity || !employeeCharge || employeeAge <= 0 || employeeYears < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', employeeName);
    formData.append('age', employeeAge);
    formData.append('city', employeeCity);
    formData.append('charge', employeeCharge);
    formData.append('years', employeeYears);
    if (employeeImage) {
      formData.append('image', employeeImage); // Añadir la imagen al FormData
    }
    if (updateId === null) return;

    try {
      const response = await Axios.put(`http://localhost:3001/update/${updateId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();

        // Reset form fields
        setEmployeeName("");
        setEmployeeAge("");
        setEmployeeCity("");
        setEmployeeCharge("");
        setEmployeeYears("");
        setEmployeeImage(null); // Reset image

        // Close the modal
        onUpdateModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to update user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user update:', error);
    }
  };

  // Función para obtener los empleados
  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  // Función para eliminar empleados
  const deleteEmployees = async () => {
    if (deleteId === null) return;

    try {
      const response = await Axios.delete(`http://localhost:3001/delete/${deleteId}`);

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Refrescar la lista de empleados después de la eliminación
        getEmployees();
        onDeleteModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to delete employee. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during employee deletion:', error);
    }
  };

  useEffect(() => {
    getEmployees();
  }, []);

  return (
    <ChakraProvider>
      <div className="App">
        <Box boxShadow='lg' bg='#0563c9' w='100%' p={4} color='white'>
          <Flex align='center' justify='space-between'>
            <Text fontSize='2xl'>Employee control panel</Text>
            <Flex>
              <IconButton
                isRound={true}
                onClick={onAddModalOpen}
                color="#0563c9"
                size="sm"
                mr={2}
                icon={<AddIcon />}
              />
              <IconButton
                ref={btnRef}
                colorScheme='teal'
                onClick={onDrawerOpen}
                isRound={true}
                size="sm"
                icon={<SettingsIcon />}
              />
            </Flex>
          </Flex>
        </Box>
        <TableContainer>
          <Table variant='simple'>
            <Thead>
              <Tr>
                <Th>Name</Th>
                <Th>Age</Th>
                <Th>City</Th>
                <Th>Charge</Th>
                <Th>Years</Th>
                <Th>Actions</Th>
              </Tr>
            </Thead>
            <Tbody>
              {employeesList.map((val) => (
                <Tr key={val.id}>
                  <Td>{val.name}</Td>
                  <Td>{val.age}</Td>
                  <Td>{val.city}</Td>
                  <Td>{val.charge}</Td>
                  <Td>{val.years}</Td>
                  <Td>
                    <IconButton
                      isRound={true}
                      colorScheme='teal'
                      icon={<EditIcon />}
                      size="sm"
                      mr={2}
                      onClick={() => {
                        setUpdateId(val.id); // Establece el ID del empleado a actualizar
                        setEmployeeName(val.name);
                        setEmployeeAge(val.age);
                        setEmployeeCity(val.city);
                        setEmployeeCharge(val.charge);
                        setEmployeeYears(val.years);
                        onUpdateModalOpen(); // Abre el modal de actualización
                      }}
                    />
                    <IconButton
                      isRound={true}
                      colorScheme='red'
                      icon={<DeleteIcon />}
                      size="sm"
                      onClick={() => {
                        setDeleteId(val.id); // Establece el ID del empleado a eliminar
                        onDeleteModalOpen(); // Abre el modal de confirmación
                      }}
                    />
                  </Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>

        {/* Modal para agregar */}
        <Modal isOpen={isAddModalOpen} onClose={onAddModalClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Add a new employee</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <FormControl>
                <Input
                  placeholder="Name"
                  mb={3}
                  value={name}
                  onChange={(event) => {
                    setName(event.target.value);
                  }}
                />
                <Input
                  placeholder="Age"
                  type="number"
                  mb={3}
                  value={age}
                  onChange={(event) => {
                    setAge(event.target.value);
                  }}
                />
                <Input
                  placeholder="City"
                  mb={3}
                  value={city}
                  onChange={(event) => {
                    setCity(event.target.value);
                  }}
                />
                <Input
                  placeholder="Charge"
                  mb={3}
                  value={charge}
                  onChange={(event) => {
                    setCharge(event.target.value);
                  }}
                />
                <Input
                  placeholder="Years"
                  type="number"
                  mb={3}
                  value={years}
                  onChange={(event) => {
                    setYears(event.target.value);
                  }}
                />
                <Input
                  placeholder="Image"
                  type="file"
                  mb={3}
                  onChange={(event) => {
                    setImage(event.target.files[0]);
                  }}
                />
              </FormControl>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={handleSubmit}>
                Submit
              </Button>
              <Button variant='ghost' onClick={onAddModalClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        {/* Modal para actualizar */}
        <Modal isOpen={isUpdateModalOpen} onClose={onUpdateModalClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Update employee information</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <FormControl>
                <Input
                  placeholder="Name"
                  mb={3}
                  value={employeeName}
                  onChange={(event) => {
                    setEmployeeName(event.target.value);
                  }}
                />
                <Input
                  placeholder="Age"
                  type="number"
                  mb={3}
                  value={employeeAge}
                  onChange={(event) => {
                    setEmployeeAge(event.target.value);
                  }}
                />
                <Input
                  placeholder="City"
                  mb={3}
                  value={employeeCity}
                  onChange={(event) => {
                    setEmployeeCity(event.target.value);
                  }}
                />
                <Input
                  placeholder="Charge"
                  mb={3}
                  value={employeeCharge}
                  onChange={(event) => {
                    setEmployeeCharge(event.target.value);
                  }}
                />
                <Input
                  placeholder="Years"
                  type="number"
                  mb={3}
                  value={employeeYears}
                  onChange={(event) => {
                    setEmployeeYears(event.target.value);
                  }}
                />
                <Input
                  placeholder="Image"
                  type="file"
                  mb={3}
                  onChange={(event) => {
                    setEmployeeImage(event.target.files[0]);
                  }}
                />
              </FormControl>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={handleSubmitUpdate}>
                Submit
              </Button>
              <Button variant='ghost' onClick={onUpdateModalClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        {/* Modal de confirmación de eliminación */}
        <AlertDialog
          isOpen={isDeleteModalOpen}
          leastDestructiveRef={cancelRef}
          onClose={onDeleteModalClose}
        >
          <AlertDialogOverlay>
            <AlertDialogContent>
              <AlertDialogHeader fontSize='lg' fontWeight='bold'>
                Delete Employee
              </AlertDialogHeader>

              <AlertDialogBody>
                Are you sure? You can't undo this action afterwards.
              </AlertDialogBody>

              <AlertDialogFooter>
                <Button ref={cancelRef} onClick={onDeleteModalClose}>
                  Cancel
                </Button>
                <Button colorScheme='red' onClick={deleteEmployees} ml={3}>
                  Delete
                </Button>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialogOverlay>
        </AlertDialog>

        <Drawer
          isOpen={isDrawer}
          placement='right'
          onClose={onDrawerClose}
          finalFocusRef={btnRef}
        >
          <DrawerOverlay />
          <DrawerContent>
            <DrawerCloseButton />
            <DrawerHeader>Options</DrawerHeader>

            <DrawerBody>
              <Wrap>
                <WrapItem>
                  <Avatar name='Dan Abrahmov' src='https://bit.ly/dan-abramov' />
                </WrapItem>
                <WrapItem>
                  <Avatar name='Kola Tioluwani' src='https://bit.ly/tioluwani-kolawole' />
                </WrapItem>
                <WrapItem>
                  <Avatar name='Kent Dodds' src='https://bit.ly/kent-c-dodds' />
                </WrapItem>
                <WrapItem>
                  <Avatar name='Ryan Florence' src='https://bit.ly/ryan-florence' />
                </WrapItem>
                <WrapItem>
                  <Avatar name='Prosper Otemuyiwa' src='https://bit.ly/prosper-baba' />
                </WrapItem>
                <WrapItem>
                  <Avatar name='Christian Nwamba' src='https://bit.ly/code-beast' />
                </WrapItem>
              </Wrap>
            </DrawerBody>

            <DrawerFooter>
              <Button variant='outline' mr={3} onClick={onDrawerClose}>
                Cancel
              </Button>
              <Button colorScheme='blue'>Save</Button>
            </DrawerFooter>
          </DrawerContent>
        </Drawer>
      </div>
    </ChakraProvider>
  );
}

export default App;
