import './App.css';
import {useState} from "react";
import React from 'react';
import Swal from 'sweetalert2';
function App() {

  const [name,setName] = useState("");
  const [age,setAge] = useState(0);
  const [city,setCity] = useState("");
  const [charge,setCharge] = useState("");
  const [years,setYears] = useState(0);

  const showData = () => {
    Swal.fire({
      title: 'Nombre',
      text: name,  // Asegúrate de que `name` esté definido en el contexto del componente
      icon: 'info',  // Puedes cambiar esto a 'success', 'warning', 'error', etc.
      confirmButtonText: 'OK'
    });
  }
  return (
    <div className="App">
     <div className="data-form">

  <label>Name:
    <input
    onChange={(event)=>{
     setName(event.target.value);
    }}
    type="text" />
  </label>

  <label>Age:
    <input 
    onChange={(event)=>{
      setAge(event.target.value);
     }}
    type="number" />
  </label>

  <label>City:
    <input 
    onChange={(event)=>{
      setCity(event.target.value);
     }}
    type="text" />
  </label>

  <label>Charge:
    <input 
    onChange={(event)=>{
      setCharge(event.target.value);
     }}
    type="text" />
  </label>

  <label>Years:
    <input 
    onChange={(event)=>{
      setYears(event.target.value);
     }}
    type="number" />
  </label>

  <button onClick={showData}>Insert</button>
</div>


    </div>
  );
}

export default App;
