import './App.css';
import { useState, useEffect } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import { Text, Box, ChakraProvider, Table, Thead, Tbody, Tfoot, Tr, Th, Td, TableContainer, Button, Modal, ModalOverlay, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton, useDisclosure, Alert, AlertIcon, AlertTitle, AlertDescription } from '@chakra-ui/react';
import { AddIcon } from '@chakra-ui/icons';
import { Input, FormControl } from '@chakra-ui/react';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState("");
  const [image, setImage] = useState(null); // Estado para la imagen
  const [employeesList, setEmployeesList] = useState([]);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const OverlayOne = () => (
    <ModalOverlay
      bg='blackAlpha.300'
      backdropFilter='blur(10px)' // Solo aplica el desenfoque sin cambiar los colores
    />
  );
  const [overlay, setOverlay] = useState(<OverlayOne />);

  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('city', city);
    formData.append('charge', charge);
    formData.append('years', years);
    if (image) {
      formData.append('image', image); // Añadir la imagen al FormData
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();

        // Reset form fields
        setName("");
        setAge("");
        setCity("");
        setCharge("");
        setYears("");
        setImage(null); // Reset image

        // Close the modal
        onClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user registration:', error);
    }
  };

  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  // Llama a getEmployees al montar el componente
  useEffect(() => {
    getEmployees();
  }, []);

  return (
    <ChakraProvider>
      <div className="App">
        <Box boxShadow='lg' bg='#0563c9' w='100%' p={4} color='white'>
          <Text fontSize='2xl'>Employee registration  
            <Button 
              top="0" 
              left="710"  
              onClick={() => {
                setOverlay(<OverlayOne />);
                onOpen();
              }} 
              colorScheme="gray" 
              size="sm" 
            >
              <AddIcon colorScheme="blue" />
            </Button>
          </Text>
        </Box>

        <Modal isOpen={isOpen} onClose={onClose} >
          {overlay}
          <ModalOverlay />
          <ModalContent position='relative'>
            <ModalHeader>Add New Employee</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <FormControl isRequired>
                <Input
                  placeholder='Name'
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setName(event.target.value)}
                  type="text"
                  value={name}
                />
                <Input
                  placeholder="Age"
                  variant="flushed"
                  size="sm"
                  onChange={(event) => setAge(event.target.value === '' ? '' : parseInt(event.target.value))}
                  type="number"
                  value={age}
                  min={1}
                />
                <Input
                  placeholder="City"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setCity(event.target.value)}
                  type="text"
                  value={city}
                />
                <Input
                  placeholder="Charge"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setCharge(event.target.value)}
                  type="text"
                  value={charge}
                />
                <Input
                  placeholder="Years"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setYears(parseInt(event.target.value, 10) || 0)}
                  type="number"
                  value={years}
                />
                <Input
                  type="file"
                  accept="image/*"
                  onChange={(event) => setImage(event.target.files[0])}
                  mt={4}
                />
              </FormControl>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme="blue" mr={3} onClick={handleSubmit}>
                Save
              </Button>
              <Button variant="ghost" onClick={onClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        <br /><br />
        <div className="tableEmployees">
          {employeesList.length > 0 ? (
            <TableContainer>
              <Table size='sm'>
                <Thead>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                    <Th>Image</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {employeesList.map((employee, index) => (
                    <Tr key={index}>
                      <Td>{employee.name}</Td>
                      <Td>{employee.age}</Td>
                      <Td>{employee.city}</Td>
                      <Td>{employee.charge}</Td>
                      <Td>{employee.years}</Td>
                      <Td>{employee.dateRegister.slice(0, -14)}</Td>
                      <Td>
                        {employee.image ? (
                          <img src={`http://localhost:3001/image/${employee.id}`} alt="Employee" width="50" height="50" />
                        ) : (
                          'No image'
                        )}
                      </Td>
                    </Tr>
                  ))}
                </Tbody>
                <Tfoot>
                  <Tr>
                    <Th>Name</Th>
                    <Th>Age</Th>
                    <Th>City</Th>
                    <Th>Charge</Th>
                    <Th>Years</Th>
                    <Th>Date Register</Th>
                    <Th>Image</Th>
                  </Tr>
                </Tfoot>
              </Table>
            </TableContainer>
          ) : (
            <Box
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Alert
                status='error'
                variant='subtle'
                flexDirection='column'
                alignItems='center'
                justifyContent='center'
                textAlign='center'
                height='200px'
                width='500px'
              >
                <AlertIcon boxSize='40px' mr={0} />
                <AlertTitle mt={4} mb={1} fontSize='lg'>
                  No registered employees
                </AlertTitle>
                <AlertDescription maxWidth='sm'>
                  At the moment there are no employees registered.
                </AlertDescription>
              </Alert>
            </Box>
          )}
        </div>
      </div>
    </ChakraProvider>
  );
}

export default App;
