import './App.css';
import { useState, useEffect, useRef } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import {
  IconButton,
  Box,
  ChakraProvider,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  TableContainer,
  Button,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
  AlertDialogCloseButton,
  Input,
  FormControl,
  Text,
} from '@chakra-ui/react';
import { AddIcon, EditIcon, DeleteIcon } from '@chakra-ui/icons';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState("");
  const [image, setImage] = useState(null); // Estado para la imagen
  const [employeesList, setEmployeesList] = useState([]);
  const [deleteId, setDeleteId] = useState(null); // Para almacenar el ID del registro a eliminar

  const {
    isOpen: isAddModalOpen,
    onOpen: onAddModalOpen,
    onClose: onAddModalClose
  } = useDisclosure();

  const {
    isOpen: isDeleteModalOpen,
    onOpen: onDeleteModalOpen,
    onClose: onDeleteModalClose
  } = useDisclosure();

  const cancelRef = useRef();

  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('city', city);
    formData.append('charge', charge);
    formData.append('years', years);
    if (image) {
      formData.append('image', image); // AÃ±adir la imagen al FormData
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();

        // Reset form fields
        setName("");
        setAge("");
        setCity("");
        setCharge("");
        setYears("");
        setImage(null); // Reset image

        // Close the modal
        onAddModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user registration:', error);
    }
  };

  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  const handleDelete = async () => {
    if (deleteId === null) return;

    try {
      const response = await Axios.delete(`http://localhost:3001/delete/${deleteId}`);

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });

        // Fetch the updated list of employees
        getEmployees();

        // Close the delete confirmation modal
        onDeleteModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to delete user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      console.error('Error during user deletion:', error);
    }
  };

  useEffect(() => {
    getEmployees();
  }, []);

  return (
    <ChakraProvider>
      <div className="App">
        <Box boxShadow='lg' bg='#0563c9' w='100%' p={4} color='white' position='relative'>
          <Text fontSize='2xl'>
            Employee registration
            <Button
              position='absolute'
              top={4}
              left={4}
              onClick={onAddModalOpen}
              colorScheme="gray"
              size="sm"
            >
              <AddIcon colorScheme="blue" />
            </Button>
          </Text>
        </Box>

        {/* Add Modal */}
        <Modal isOpen={isAddModalOpen} onClose={onAddModalClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Add New Employee</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <FormControl isRequired>
                <Input
                  placeholder='Name'
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setName(event.target.value)}
                  type="text"
                  value={name}
                />
                <Input
                  placeholder="Age"
                  variant="flushed"
                  size="sm"
                  onChange={(event) => setAge(event.target.value === '' ? '' : parseInt(event.target.value))}
                  type="number"
                  value={age}
                  min={1}
                />
                <Input
                  placeholder="City"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setCity(event.target.value)}
                  type="text"
                  value={city}
                />
                <Input
                  placeholder="Charge"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setCharge(event.target.value)}
                  type="text"
                  value={charge}
                />
                <Input
                  placeholder="Years"
                  variant='flushed'
                  size='sm'
                  onChange={(event) => setYears(parseInt(event.target.value, 10) || 0)}
                  type="number"
                  value={years}
                />
                <Input
                  type="file"
                  variant='flushed'
                  accept="image/*"
                  onChange={(event) => setImage(event.target.files[0])}
                  mt={4}
                />
              </FormControl>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme="blue" mr={3} onClick={handleSubmit}>
                Save
              </Button>
              <Button variant="ghost" onClick={onAddModalClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        {/* Delete Confirmation Modal */}
        <AlertDialog
          motionPreset='slideInBottom'
          leastDestructiveRef={cancelRef}
          onClose={onDeleteModalClose}
          isOpen={isDeleteModalOpen}
          isCentered
        >
          <AlertDialogOverlay bg='blackAlpha.300' backdropFilter='blur(10px)' />
          <AlertDialogContent>
            <AlertDialogHeader>Delete Record?</AlertDialogHeader>
            <AlertDialogCloseButton />
            <AlertDialogBody>
              Are you sure you want to delete this record? It will be removed permanently.
            </AlertDialogBody>
            <AlertDialogFooter>
              <Button ref={cancelRef} onClick={onDeleteModalClose}>
                No
              </Button>
              <Button colorScheme='red' ml={3} onClick={handleDelete}>
                Yes
              </Button>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>

        <TableContainer>
          <Table variant='simple'>
            <Thead>
              <Tr>
                <Th>Name</Th>
                <Th>Age</Th>
