import './App.css';
import { useState, useEffect, useRef } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import {
  IconButton,
  ChakraProvider,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  TableContainer,
  Button,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  Input
} from '@chakra-ui/react';
import { AddIcon, EditIcon, DeleteIcon } from '@chakra-ui/icons';

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [city, setCity] = useState("");
  const [charge, setCharge] = useState("");
  const [years, setYears] = useState("");
  const [image, setImage] = useState(null);
  const [employeesList, setEmployeesList] = useState([]);
  const [employeeName, setEmployeeName] = useState("");
  const [employeeAge, setEmployeeAge] = useState("");
  const [employeeCity, setEmployeeCity] = useState("");
  const [employeeCharge, setEmployeeCharge] = useState("");
  const [employeeYears, setEmployeeYears] = useState("");
  const [employeeImage, setEmployeeImage] = useState(null);
  const [deleteId, setDeleteId] = useState(null);
  const [updateId, setUpdateId] = useState(null);

  const { isOpen: isAddModalOpen, onOpen: onAddModalOpen, onClose: onAddModalClose } = useDisclosure();
  const { isOpen: isDeleteModalOpen, onOpen: onDeleteModalOpen, onClose: onDeleteModalClose } = useDisclosure();
  const { isOpen: isUpdateModalOpen, onOpen: onUpdateModalOpen, onClose: onUpdateModalClose } = useDisclosure();
  const cancelRef = useRef();

  const handleSubmit = async () => {
    if (!name || !city || !charge || age <= 0 || years < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('city', city);
    formData.append('charge', charge);
    formData.append('years', years);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await Axios.post('http://localhost:3001/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });
        getEmployees();
        setName("");
        setAge("");
        setCity("");
        setCharge("");
        setYears("");
        setImage(null);
        onAddModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to register user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  const handleSubmitUpdate = async () => {
    if (!employeeName || !employeeCity || !employeeCharge || employeeAge <= 0 || employeeYears < 0) {
      Swal.fire({
        title: 'Error',
        text: 'Please fill out all fields correctly.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', employeeName);
    formData.append('age', employeeAge);
    formData.append('city', employeeCity);
    formData.append('charge', employeeCharge);
    formData.append('years', employeeYears);
    if (employeeImage) {
      formData.append('image', employeeImage);
    }

    try {
      const response = await Axios.put(`http://localhost:3001/update/${updateId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });
        getEmployees();
        setEmployeeName("");
        setEmployeeAge("");
        setEmployeeCity("");
        setEmployeeCharge("");
        setEmployeeYears("");
        setEmployeeImage(null);
        onUpdateModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to update user. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  const getEmployees = async () => {
    try {
      const response = await Axios.get('http://localhost:3001/employees');
      setEmployeesList(response.data);
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to fetch employees. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  const deleteEmployees = async () => {
    try {
      const response = await Axios.delete(`http://localhost:3001/delete/${deleteId}`);

      if (response.data.success) {
        Swal.fire({
          title: 'Success',
          text: response.data.message,
          icon: 'success',
          confirmButtonText: 'OK'
        });
        getEmployees();
        onDeleteModalClose();
      } else {
        Swal.fire({
          title: 'Error',
          text: response.data.message,
          icon: 'error',
          confirmButtonText: 'OK'
        });
      }
    } catch (error) {
      Swal.fire({
        title: 'Error',
        text: 'Failed to delete employee. Please try again later.',
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  useEffect(() => {
    getEmployees();
  }, []);

  return (
    <ChakraProvider>
      <div className="App">
        <IconButton
          isRound={true}
          onClick={onAddModalOpen}
          color="#0563c9"
          size="sm"
          mr={2}
          icon={<AddIcon />}
        />
        <TableContainer>
          <Table variant='simple'>
            <Thead>
              <Tr>
                <Th>Name</Th>
                <Th>Age</Th>
                <Th>City</Th>
                <Th>Charge</Th>
                <Th>Years</Th>
                <Th>Actions</Th>
              </Tr>
            </Thead>
            <Tbody>
              {employeesList.map((val) => (
                <Tr key={val.id}>
                  <Td>{val.name}</Td>
                  <Td>{val.age}</Td>
                  <Td>{val.city}</Td>
                  <Td>{val.charge}</Td>
                  <Td>{val.years}</Td>
                  <Td>
                    <IconButton
                      isRound={true}
                      colorScheme='teal'
                      icon={<EditIcon />}
                      size="sm"
                      mr={2}
                      onClick={() => {
                        setUpdateId(val.id);
                        setEmployeeName(val.name);
                        setEmployeeAge(val.age);
                        setEmployeeCity(val.city);
                        setEmployeeCharge(val.charge);
                        setEmployeeYears(val.years);
                        onUpdateModalOpen();
                      }}
                    />
                    <IconButton
                      isRound={true}
                      colorScheme='red'
                      icon={<DeleteIcon />}
                      size="sm"
                      onClick={() => {
                        setDeleteId(val.id);
                        onDeleteModalOpen();
                      }}
                    />
                  </Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>

        <Modal isOpen={isAddModalOpen} onClose={onAddModalClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Add a new employee</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Input
                placeholder="Name"
                mb={3}
                value={name}
                onChange={(event) => {
                  setName(event.target.value);
                }}
              />
              <Input
                placeholder="Age"
                type="number"
                mb={3}
                value={age}
                onChange={(event) => {
                  setAge(event.target.value);
                }}
              />
              <Input
                placeholder="City"
                mb={3}
                value={city}
                onChange={(event) => {
                  setCity(event.target.value);
                }}
              />
              <Input
                placeholder="Charge"
                mb={3}
                value={charge}
                onChange={(event) => {
                  setCharge(event.target.value);
                }}
              />
              <Input
                placeholder="Years"
                type="number"
                mb={3}
                value={years}
                onChange={(event) => {
                  setYears(event.target.value);
                }}
              />
              <Input
                placeholder="Image"
                type="file"
                mb={3}
                onChange={(event) => {
                  setImage(event.target.files[0]);
                }}
              />
            </ModalBody>
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={handleSubmit}>
                Submit
              </Button>
              <Button variant='ghost' onClick={onAddModalClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>

        <Modal isOpen={isUpdateModalOpen} onClose={onUpdateModalClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Update employee information</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Input
                placeholder="Name"
                mb={3}
                value={employeeName}
                onChange={(event) => {
                  setEmployeeName(event.target.value);
                }}
              />
              <Input
                placeholder="Age"
                type="number"
                mb={3}
                value={employeeAge}
                onChange={(event) => {
                  setEmployeeAge(event.target.value);
                }}
              />
              <Input
                placeholder="City"
                mb={3}
                value={employeeCity}
                onChange={(event) => {
                  setEmployeeCity(event.target.value);
                }}
              />
              <Input
                placeholder="Charge"
                mb={3}
                value={employeeCharge}
                onChange={(event) => {
                  setEmployeeCharge(event.target.value);
                }}
              />
              <Input
                placeholder="Years"
                type="number"
                mb={3}
                value={employeeYears}
                onChange={(event) => {
                  setEmployeeYears(event.target.value);
                }}
              />
              <Input
                placeholder="Image"
                type="file"
                mb={3}
                onChange={(event) => {
                  setEmployeeImage(event.target.files[0]);
                }}
              />
            </ModalBody>
            <ModalFooter>
              <Button colorScheme='blue' mr={3} onClick={handleSubmitUpdate}>
                Submit
              </Button>
              <Button variant='ghost' onClick={onUpdateModalClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </div>
    </ChakraProvider>
  );
}

export default App;
